#include "../xs.h"

int main(int argc, char *argv[])
{
    xs *string1 = xs_new(&xs_literal_empty(), "");
    xs *string2 = xs_new(&xs_literal_empty(), "");
    xs *string3 = xs_new(&xs_literal_empty(), "");

    printf("string1: %p\n", string1->ptr + 4);
    printf("string2: %p\n", string2->ptr + 4);
    printf("string3: %p\n", string3->ptr + 4);
}